create table recipe_menu_item
(
    final_amount        double precision                                                  not null,
    init_amount         double precision                                                  not null,
    losses_amount       double precision                                                  not null,
    losses_percentage   double precision                                                  not null,
    id                  bigint default nextval('menu_item_recipe_id_generator'::regclass) not null
        primary key,
    menu_item_id        bigint                                                            not null
        constraint fk_recipe_menu_item_menu_item_id_flow_id
            references menu_item,
    source_id           bigint                                                            not null,
    station_id          bigint                                                            not null,
    source_type         varchar(255)                                                      not null,
    measurement_unit_id bigint
        constraint recipe_menu_item__measurement_id_fk
            references measurement,
    priority            bigint default 100                                                not null,
    constraint recipe_menu_item__mi_si_unique_pk
        unique (menu_item_id, source_id, station_id)
);

alter table recipe_menu_item
    owner to root;

