create table recipe_menu_item_aud
(
    final_amount        double precision,
    init_amount         double precision,
    losses_amount       double precision,
    losses_percentage   double precision,
    rev                 integer not null
        constraint fk_recipe_menu_item_aud_rev_revinfo_rev
            references revinfo,
    revtype             smallint,
    id                  bigint  not null,
    menu_item_id        bigint,
    source_id           bigint,
    station_id          bigint,
    source_type         varchar(255),
    priority            bigint,
    measurement_unit_id bigint,
    primary key (rev, id)
);

alter table recipe_menu_item_aud
    owner to root;

