create table recipe_prepack_aud
(
    final_amount        double precision,
    init_amount         double precision,
    losses_amount       double precision,
    losses_percentage   double precision,
    rev                 integer  not null
        constraint fk_recipe_prepack_aud_rev_revinfo_rev
            references revinfo,
    revtype             smallint not null,
    id                  bigint   not null,
    prepack_id          bigint,
    source_id           bigint,
    source_type         varchar(255),
    priority            bigint,
    measurement_unit_id bigint,
    primary key (rev, id)
);

alter table recipe_prepack_aud
    owner to root;

